const texts = [
  $node["Reescrever Roteiro com Gancho Sensacional"].json.message.content,
  $node["Gerar Capítulo 1"].json.message.content,
  $node["Gerar Capítulo 2"].json.message.content,
  $node["Gerar Capítulo 3"].json.message.content,
  $node["Gerar Capítulo 4"].json.message.content,
  $node["Gerar Capítulo 5"].json.message.content,
  $node["Gerar Capítulo 6"].json.message.content,
  $node["Gerar Capítulo 7"].json.message.content,
  $node["Gerar Capítulo 8"].json.message.content,
  $node["Gerar Capítulo Final"].json.message.content,
];

function cleanChapter(s) {
  if (!s) return "";
  return s
    // remove "Capítulo 0/1/..." no início (variações de maiúscula/minúscula; com/sem dois-pontos)
    .replace(/^\s*cap[ií]tulo\s*\d+\s*:?\s*/gmi, "")
    // limpa espaços à direita e normaliza quebras
    .replace(/[ \t]+$/gm, "")
    .replace(/\n{3,}/g, "\n\n")
    .trim();
}

const cleaned = texts.map(t => cleanChapter(t || ""));
// separador mínimo entre capítulos
const sep = "\n\n";
const finalText = cleaned.join(sep);

return [{ json: { text: finalText } }];

function generateUUIDv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

return [
  {
    json: {
      uuid: generateUUIDv4()
    }
  }
];

let t = $node["Concatenar Capítulos"].json.text || "";

// (A) Unificar canon: "Cristina" NÃO é mãe da narradora (trocas conservadoras)
t = t.replace(/\bvoz\s+joven\s+de\s+mi\s+madre\b/gi, "voz joven de Cristina");
t = t.replace(/\bmi\s+madre\b/gi, "Cristina");

// (A.1) Remover cabeçalhos tipo "CAPÍTULO X"
t = t.replace(/^\s*cap[ií]tulo\s+\d+\s*:?.*$/gim, "");

// (B) Garantia anti-ruído: remove marcas [CTA] se vierem
t = t.replace(/\[CTA\][^\n]*\n?/gi, "");

// (C) Sanitização final: colapsa múltiplas linhas vazias
t = t.replace(/\n{3,}/g, "\n\n").trim();

// (D) Injeção de CTAs diegéticos se o bloco NÃO traz nenhum
const ctaPool = [
  "Si alguien me oye, cuéntenme desde dónde para no sentirme tan sola esta noche.",
  "Guarden este pedacito por si mañana me hacen callar; quiero saber que sigue vivo.",
  "Si de verdad quieren que siga, díganmelo bajito; junto fuerzas para contarlo.",
  "Déjenme una seña —un país, una calle— para no perderme en esta oscuridad.",
  "Si estoy diciendo lo correcto, háganmelo saber con una palabra; yo entenderé.",
  "Guarden esto como prueba; por si me cierran la puerta, alguien lo tendrá."
];

function injectCTAs(text) {
  const parts = text.split(/\n{2,}/); // aproxima capítulos por blocos
  const rebuilt = [];
  let ctaIdx = 0;

  for (let block of parts) {
    const hasCTA = /(\bcuéntenme\b|\bguarden\b|\bdíganmelo\b|déjenme una seña|háganmelo saber)/i.test(block);

    if (!hasCTA) {
      const lines = block.split("\n");
      const pos = Math.max(1, Math.floor(lines.length * 0.6));
      const cta1 = ctaPool[ctaIdx % ctaPool.length]; ctaIdx++;
      lines.splice(pos, 0, cta1);

      if (lines.length > 16) {
        const pos2 = Math.min(lines.length - 2, Math.floor(lines.length * 0.8));
        const cta2 = ctaPool[ctaIdx % ctaPool.length]; ctaIdx++;
        lines.splice(pos2, 0, cta2);
      }
      block = lines.join("\n");
    }

    // Não deixar CTA como última linha (remove a linha inteira se for CTA)
    block = block.replace(/\n(?:Si alguien|Guarden|Si de verdad|Déjenme una seña|Si estoy diciendo|Guarden esto)[^\n]*\s*$/i, "");

    rebuilt.push(block.trim());
  }

  return rebuilt.join("\n\n").trim();
}

const AUTO_INJECT = true;
if (AUTO_INJECT) t = injectCTAs(t);

return [{ json: { text: t } }];
